import React, { useState } from "react";
import search from "../search.svg";
import emojis from "../emojis.svg";
import mic from "../mic.svg";
import attach from "../attach.svg";
export default function ChatFooter({ sendText }) {
  const [text, setText] = useState("");

  return (
    <div
      style={{
        backgroundColor: "#e2e2e2",
        padding: "8px",
        height: "60px",
        display: "flex",
        alignItems: "center",
      }}
    >
      <img
        src={emojis}
        style={{
          height: "25px",
          width: "25px",
          marginLeft: "10px",
        }}
      />

      <img
        src={attach}
        style={{
          height: "25px",
          width: "25px",
          marginLeft: "10px",
        }}
      />

      <input
        placeholder="Type a message"
        style={{
          height: "48px",
          width: "87%",
          borderRadius: "15px",
          outline: "none",
          border: "none",
          paddingLeft: "20px",
          paddingRight: "20px",
          marginLeft: "10px",
        }}
        onChange={(e) => setText(e.target.value)}
        onKeyDown={(e) => sendText(e)}
      />
      <img
        src={mic}
        style={{
          height: "25px",
          width: "25px",
          marginLeft: "10px",
        }}
      />
    </div>
  );
}
