import React, { useContext, useState } from "react";
import bodyBg from "../bodyBg.jpg";
import ChatFooter from "./ChatFooter.js";
import { AuthContext } from "../context/AccountAuth";
import { newMessage } from "../../services/api";

export default function ChatBody({ person, conversation }) {
  const { account } = useContext(AuthContext);
  const [value, setValue] = useState("");

  const sendText = async (e) => {
    if (e.keyCode === 13) {
      let message = {
        senderId: account.sub,
        receiverId: person.sub,
        conversationId: conversation._id,
        type: "text",
        text: value,
      };

      await newMessage(message);
      setValue("");
    }
  };

  return (
    <>
      <div style={{ backgroundImage: `url(${bodyBg})`, height: "78vh" }}></div>
      <ChatFooter sendText={sendText} setValue={setValue} value={value} />
    </>
  );
}
