import axios from "axios";
import { toast } from "react-toastify";
export const addUser = async (data) => {
  try {
    console.log(data);
    const res = await axios.post(`${process.env.REACT_API}/add`, data);

    if (!res.data.success) {
      toast.success(res.data.message);
    }
  } catch (error) {
    console.log(error);
  }
};

export const getUser = async () => {
  try {
    const res = await axios.get(`${process.env.REACT_API}/users`);
    console.log(res.data);
  } catch (error) {
    console.log(error);
  }
};
