import React, { useContext, useState } from "react";
import { AuthContext } from "./context/AccountAuth";
import arrow from "./arrow.svg";

export default function Drawer({ isOpen }) {
  const { account } = useContext(AuthContext);
  const disp = isOpen === true ? "none" : "block";
  return (
    <div
      className="drawer"
      style={{
        display: disp,
        zIndex: "3",
        position: "absolute",
      }}
    >
      <div
        style={{
          width: "450px",
          height: "640px",
          boxShadow: "0px 0px 30px 0px",
        }}
      >
        <div
          style={{
            display: "flex",
            backgroundColor: "green",
            padding: "80px 10px 10px 10px",
          }}
        >
          <img
            src={arrow}
            style={{
              height: "30px",
              width: "30px",
              margin: "5px",
            }}
          />
          <h3 style={{ color: "white", marginLeft: "30px" }}>Profile</h3>
        </div>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "rgba(0,0,0,0.05",
            height: "200px",
          }}
        >
          <img
            src={account.picture}
            style={{ borderRadius: "50%", height: "150px", width: "150px" }}
          />
        </div>
        <div style={{ boxShadow: "1px 1px" }}>
          <p>Your name</p>
        </div>
      </div>
    </div>
  );
}
