import React, { useContext } from "react";
import { useState } from "react";
import { useEffect } from "react";
import { getUser } from "../services/api";
import { AuthContext } from "./context/AccountAuth";

export default function Conversations() {
  const [users, setUsers] = useState([]);
  const { account, setPerson } = useContext(AuthContext);

  const getPerson = () => {
    console.log("working");
  };

  useEffect(() => {
    const fetchData = async () => {
      const data = await getUser();
      setUsers(data);
      console.log("hello", data);
    };

    fetchData();
  }, []);
  return (
    <div style={{ height: "81vh", overflow: "overlay" }}>
      <ul style={{ listStyle: "none", width: "100%", padding: "5px" }}>
        {users.map(
          (user) =>
            user.sub !== account.sub && (
              <li>
                <div className="conversation" onClick={getPerson}>
                  <img
                    src={user.picture}
                    style={{
                      height: "50px",
                      width: "50px",
                      borderRadius: "50%",
                      marginRight: "10px",
                    }}
                  />
                  {user.name}
                </div>
              </li>
            )
        )}
      </ul>
    </div>
  );
}
