import React, { useState } from "react";

export default function Drawer({ isOpen }) {
  const disp = isOpen === true ? "none" : "block";
  return (
    <div
      className="drawer"
      style={{
        display: disp,
        zIndex: "3",
        position: "absolute",
        transition: "opacity 0.5s ease", // Add the transition property
        opacity: isOpen ? 0 : 1, // Add opacity to control the fade
      }}
    >
      <div
        style={{
          width: isOpen ? "450px" : "0",
          height: "640px",
          backgroundColor: "red",
        }}
      >
        hello
      </div>
    </div>
  );
}
