import axios from "axios";
import { toast } from "react-toastify";
export const addUser = async (data) => {
  try {
    console.log(data);
    const res = await axios.post(`http://localhost:8000/add`, data);

    if (!res.data.success) {
      toast.success(res.data.message);
    }
  } catch (error) {
    console.log(error);
  }
};
