import React, { useContext } from "react";
import { formatDate } from "../../utils/common-utils.js";
import { AuthContext } from "../context/AccountAuth.js";

export default function Message({ message }) {
  const { account } = useContext(AuthContext);
  return (
    <>
      {account.sub == message.senderId ? (
        <div
          style={{
            backgroundColor: "green",
            maxWidth: "60%",
            width: "fit-content",
            padding: "5px",
            margin: "5px",
            borderRadius: "10px",
            wordBreak: "break-word",
            marginLeft: "auto",
            minWidth: "15%",
            boxShadow: "2px 20px",
          }}
        >
          <h6
            style={{
              margin: "0",
              padding: "0",
              wordBreak: "break-word",
              color: "white",
            }}
          >
            {message.text}
          </h6>
          <p
            style={{
              margin: "0",
              padding: "0",
              wordBreak: "break-all",
              fontSize: "10px",
              color: "#919191",
              textAlign: "end",
            }}
          >
            {formatDate(message.createdAt)}
          </p>
        </div>
      ) : (
        <div
          style={{
            backgroundColor: "white",
            maxWidth: "60%",
            width: "fit-content",
            padding: "5px",
            margin: "5px",
            borderRadius: "10px",
            wordBreak: "break-word",
            minWidth: "15%",
          }}
        >
          <h6 style={{ margin: "0", padding: "0", wordBreak: "break-word" }}>
            {message.text}
          </h6>
          <p
            style={{
              margin: "0",
              padding: "0",
              wordBreak: "break-all",
              fontSize: "10px",
              color: "#919191",
              textAlign: "end",
            }}
          >
            {formatDate(message.createdAt)}
          </p>
        </div>
      )}
    </>
  );
}
